mapper.select:
select bmdm_id, bmmc, (select bmmc from zftal_xtgl_bmdmb where bmdm_id_ls = bmdm_id) as bmdm_id_ls, ywmc, bmjc, cydm_id_bmlb, sfyx, sfst, bmfzr, kkxy from zftal_xtgl_bmdmb
and bmmc like '%' || #{bmmc} || '%'
and bmjc like '%' || #{bmjc}|| '%'
and cycm_id_bmlb = #{cydm_id_bmlb}
and sfyx = #{sfyx}
and sfst = #{sfst}
and kkxy = #{kkxy}

select bmdm_id, bmmc, kkxy,bmdm_id_ls from zftal_xtgl_bmdmb bm where bm.sfyx = '1'
and bm.kkxy= #{kkxy}
and bm.bmdm_id_ls= #{bmdm_id_ls}
